<?php	header("Content-Type: application/json");	$response= array();	include("conexi.php");	$link=Conectarse();		if($_POST["cerrado"] == "Cerrado"){		$response["insertaTurno"] =  insertaTurno($link, $_POST["efectivo_inicial"], $_POST["id_sucursal"]);		$id_turno =  $response["insertaTurno"]["id_turno"];			}	else{		$id_turno = $_POST["turno"];	}		// setcookie("id_usuarios", $id_usuario,  0, "/");	// setcookie("permiso_usuarios", $row["permiso_usuarios"],  0, "/");	// setcookie("nombre_usuarios", $nombre_usuario,  0, "/");	// setcookie("id_turnos", $id_turno,  0, "/");	setcookie("id_sucursal", $_POST["id_sucursal"],  0, "/");	setcookie("efectivo_inicial", $_POST["efectivo_inicial"],  0, "/");	setcookie("id_turnos", $_POST["turno"],  0, "/");				echo json_encode($response);			function insertaTurno($link, $efectivo_inicial, $id_sucursal){				$consulta = "INSERT INTO turnos SET 		efectivo_inicial ='$efectivo_inicial',		fecha_inicio_turnos = CURDATE(),		hora_inicios = CURTIME(),		id_sucursal =$id_sucursal		";				if(mysqli_query($link, $consulta)){			return array(			"estatus" => "success", 			"id_turno" =>  mysqli_insert_id($link), 			"consulta" => $consulta			);		}		else{			return array(			"estatus" => "error", 			"mensaje" => mysqli_error($link), 			"consulta" => $consulta			);		}			}			function actualizaEfectivo($link, $turno, $efectivo_inicial){				$consulta = "UPDATE turnos SET efectivo_inicial ='$efectivo_inicial' WHERE id_turnos = '$turno'";				if(mysqli_query($link, $consulta)){			return array(			"estatus" => "success", 			"consulta" => $consulta			);		}		else{			return array(			"estatus" => "error", 			"mensaje" => mysqli_error($link), 			"consulta" => $consulta			);		}			}						?>		